conversationMessage.preProcessMessage:
var hoursSpec = [
    "REG_HOURS~9:00~22:00",
    "AFTER_HOURS~22:00~9:00",
    "FRIDAY|WKEND_REG_HOURS~9:00~22:00",
    "FRIDAY|WKEND_AFTER_HOURS~22:00~9:00",
    "SATURDAY|WKEND_REG_HOURS~12:00~21:00",
    "SATURDAY|WKEND_AFTER_HOURS~21:00~12:00",
    "SUNDAY|WKEND_REG_HOURS~12:00~21:00",
    "SUNDAY|WKEND_AFTER_HOURS~21:00~12:00",
];





var type = botContext.getHoursType(hoursSpec, "Asia/Kolkata");
botContext.printDebugMessage(type);





// TEST FOR type and set the transferMessage to result
botContext.printDebugMessage("before switch statement");
switch(type){
  case "REG_HOURS":
  case "WKEND_REG_HOURS":
    botContext.setBotVariable("working","true",true,false);
    botContext.printDebugMessage("Inside working hours");
    botContext.setTriggerNextMessage('WorkingHour-yes');
    break;



  case "AFTER_HOURS":
  //case "SATURDAY|WKEND_AFTER_HOURS":
  //case "SUNDAY|WKEND_AFTER_HOURS":
   case "WKEND_AFTER_HOURS":
     botContext.printDebugMessage("Inside not wokring hours");
    botContext.setBotVariable("working","false",true,false);

    botContext.setTriggerNextMessage('WorkingHour-no');
    break;
}

var d = new Date(); 
if (botContext.getBotVariable('lastVisited') != null) { 
    botContext.setTriggerNextMessage('Welcome Back');
} else { 
    botContext.setBotVariable('lastVisited',d,false,true); 
}

globalFunctions.methods:
function __initConversation() { 

  var sharkVisitorId = botContext.getLPEngagementAttribute("sharkVisitorId");
  var sharkSessionId = botContext.getLPEngagementAttribute("sharkSessionId");
  var BearerToken = botContext.getLPEngagementAttribute("BearerToken");

  var convId = botContext.getConversationId();

  botContext.setBotVariable("visitorId",sharkVisitorId,true,false);
  botContext.setBotVariable("sessionId",sharkSessionId,true,false);
  botContext.setBotVariable("BearerToken",BearerToken,true,false);
  botContext.setBotVariable("convId",convId,true,false);
}
